function scrollFunction(){document.body.scrollTop>20||document.documentElement.scrollTop>20?document.getElementById("myBtn").style.display="block":document.getElementById("myBtn").style.display="none"}function topFunction(){document.body.scrollTop=0,document.documentElement.scrollTop=0}$(function(){$("a[href*=#]:not([href=#])").click(function(){if(location.pathname.replace(/^\//,"")==this.pathname.replace(/^\//,"")&&location.hostname==this.hostname){var n=$(this.hash);if((n=n.length?n:$("[name="+this.hash.slice(1)+"]")).length)return $("html,body").animate({scrollTop:n.offset().top},1e3),!1}})}),window.onscroll=function(){scrollFunction()};